import 'dart:convert';

import 'package:flutter/material.dart';
import 'package:dio/dio.dart';
import 'package:image_picker/image_picker.dart';
import 'dart:convert';
import 'package:font_awesome_flutter/font_awesome_flutter.dart';
import 'dart:io';

class Screen2 extends StatefulWidget {
  @override
  _Screen2 createState() => _Screen2();
}

class _Screen2 extends State<Screen2> {
  String mytext = "";
  String buxt = "Send Image";
  File img;
  void myhttp() async {
    img = await ImagePicker.pickImage(source: ImageSource.gallery)
        .whenComplete(() {
      setState(() {
        img = img;
        mytext = "";
      });
    });
    String baseimg = base64Encode(img.readAsBytesSync());
    //print(baseimg);
    Dio dio = new Dio();
    try {
      String url = "https://api.ocr.space/parse/image";
      FormData formData = new FormData.fromMap({
        "apikey": "da8546ce3d88957",
        "base64image": "data:image/png;base64," + baseimg,
        "filetype": "PNG",
        "language": "eng",
        //"url":"https://pluspng.com/img-png/download-love-text-png-images-transparent-gallery-advertisement-500.png"
        // "base64image":"data:image/png;base64,"
      });

      Response response;
      print("Going to send req");
      response = await dio.post(url,
          data: formData, options: Options(contentType: 'multipart/form-data'));
      print(response.statusCode);
      var res = json.decode(response.toString());
      String s = res['ParsedResults'][0]['ParsedText'];
      print(s);
      setState(() {
        mytext = s;
        buxt = "Send Image";
      });
    } catch (e) {
      print(e);
    }
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
        body: Container(
            padding: EdgeInsets.all(10),
            margin: EdgeInsets.all(10),
            child: ListView(
              shrinkWrap: true,
              //mainAxisAlignment: MainAxisAlignment.center,
              children: <Widget>[
                Center(
                  child: Container(
                      height: 400,
                      width: MediaQuery.of(context).size.width,
                      child: (img == null)
                          ? Text(
                              "Image Not Uploaded",
                              textAlign: TextAlign.center,
                            )
                          : Image.file(img)),
                ),
                Row(
                  mainAxisAlignment: MainAxisAlignment.center,
                  children: <Widget>[
                    Container(
                        margin: EdgeInsets.all(10),
                        width: MediaQuery.of(context).size.width * 0.8,
                        decoration: BoxDecoration(color: Colors.lightBlue[50]),
                        child: Text(mytext))
                  ],
                ),
                Row(
                  mainAxisAlignment: MainAxisAlignment.center,
                  children: <Widget>[
                    FloatingActionButton.extended(
                      icon: Icon(FontAwesomeIcons.paperPlane),
                      isExtended: true,
                      label: Text(buxt),
                      onPressed: () {
                        myhttp();
                        setState(() {
                          buxt = "Uploading....";
                        });
                      },
                    )
                  ],
                )
              ],
            )));
  }
}
